examples.code:
// Start animation
$( "#go" ).click(function() {
  $( ".block" ).animate({ left: "+=100px" }, 2000 );
});

// Stop animation when button is clicked
$( "#stop" ).click(function() {
  $( ".block" ).stop();
});

// Start animation in the opposite direction
$( "#back" ).click(function() {
  $( ".block" ).animate({ left: "-=100px" }, 2000 );
});

examples.css:
div {
  position: absolute;
  background-color: #abc;
  left: 0px;
  top: 30px;
  width: 60px;
  height: 60px;
  margin: 5px;
}

examples.code:
var $block = $( ".block" );

// Toggle a sliding animation animation
$( "#toggle" ).on( "click", function() {
  $block.stop().slideToggle( 1000 );
});

examples.css:
.block {
  background-color: #abc;
  border: 2px solid black;
  width: 200px;
  height: 80px;
  margin: 10px;
}
